/**
 * Date Ago
 * @param  object $ jQuery variable 
 * @param  object options 
 * @return object
 */
!function(t){t.fn.dateAgo=function(e){var s=t.extend({texts:{now:"刚刚",minutes:"分钟前",hour:"小时前",hours:"小时前",yesterday:"昨天",days:"天前"}},e);return this.each(function(){var e=t(this).text().replace(" ","T")+"Z",a=new Date(new Date(e).getTime()-288e5),r=new Date,o=r.getTime()-a.getTime(),n="";if(864e5>o)if(36e5>o){var i=Math.floor(o/6e4);n=1>=i?s.texts.now:i.toString()+" "+s.texts.minutes}else{var h=Math.floor(o/36e5);n=h.toString()+(1==h?" "+s.texts.hour:" "+s.texts.hours)}else{var u=Math.floor(o/864e5);n=1==u?s.texts.yesterday:u.toString()+" "+s.texts.days}t(this).addClass("dateAgo").text(n)})}}(jQuery);